import React, { Component, Fragment } from 'react';
import { Tabs, WhiteSpace, SearchBar, Flex } from 'antd-mobile';
const tabs = [
    { title: '饮品' },
    { title: '甜品' },
    { title: '书本' },
];
const PlaceHolder = ({ className = '', ...restProps }) => (
    <div className={`${className} placeholder`} {...restProps} style={{ height: 50 }}>Block</div>
);


class Shope extends Component {
    constructor(props) {
        super(props)
        this.state = {

        }
    }

    render() {
        return (
            <Fragment>
                <div className="col" >
                    <SearchBar placeholder="Search Book" maxLength={8} />
                    <div style={{ height: 400 }} >
                        <WhiteSpace />
                        <Tabs tabs={tabs}
                            initalPage={'t2'}
                            tabBarPosition="left"
                            tabDirection="vertical"
                        >
                            <div style={{ height: '400px', backgroundColor: '#fff', padding: '5px' }}>
                                <Flex  >
                                    <Flex.Item  style={{backgroundColor:"#f44336"}}><div  style={{ textAlign: "center",color:'white',height:10}}>特色</div></Flex.Item>
                                    <Flex.Item><p style={{ textAlign: "center" }}>奶茶</p></Flex.Item>
                                    <Flex.Item><p style={{ textAlign: "center" }}>茶</p></Flex.Item>
                                    <Flex.Item><p style={{ textAlign: "center" }}>果汁</p></Flex.Item>

                                </Flex>
                                <Flex style={{ marginTop: "5px" }}>
                                    <Flex.Item><PlaceHolder /></Flex.Item>
                                    <Flex.Item><PlaceHolder /></Flex.Item>
                                    <Flex.Item><PlaceHolder /></Flex.Item>
                                    <Flex.Item><PlaceHolder /></Flex.Item>
                                </Flex>
                            </div>
                            <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', height: '400px', backgroundColor: '#fff' }}>
                                Content of second tab
      </div>
                            <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', height: '400px', backgroundColor: '#fff' }}>
                                Content of third tab
      </div>
                        </Tabs>
                        <WhiteSpace />
                    </div>

                </div>

            </Fragment>
        );
    }

}

export default Shope;
