import React, { Component, Fragment } from 'react';
import { NoticeBar, WhiteSpace, Carousel, WingBlank } from 'antd-mobile';


const PlaceHolder = ({ className = '', ...restProps }) => (
    <div className={`${className} placeholder`} {...restProps}>今日推荐饮品</div>
);
class Home extends Component {

    componentDidMount() {
        // simulate img loading
        setTimeout(() => {
            this.setState({
                data: ['AiyWuByWklrrUDlFignR', 'TekJlZRVCjLFexlOCuWn', 'IJOtIlfsYdTyaDTRVrLI'],
            });
        }, 100);
    }


    constructor(props) {
        super(props)


        this.state = {
            data: ['1', '2', '3'],
            imgHeight: 176,
        }
    }

    render() {
        return (
            <Fragment>
                <WingBlank>
                    <Carousel
                        autoplay={true}
                        infinite
                        beforeChange={(from, to) => console.log(`slide from ${from} to ${to}`)}
                        afterChange={index => console.log('slide to', index)}
                    >
                        {this.state.data.map(val => (
                            <a
                                key={val}
                                href="http://www.alipay.com"
                                style={{ display: 'inline-block', width: '100%', height: this.state.imgHeight }}
                            >
                                <img
                                    src={`https://zos.alipayobjects.com/rmsportal/${val}.png`}
                                    alt=""
                                    style={{ width: '100%', verticalAlign: 'top' }}
                                    onLoad={() => {
                                        // fire window resize event to change height
                                        window.dispatchEvent(new Event('resize'));
                                        this.setState({ imgHeight: 'auto' });
                                    }}
                                />
                            </a>
                        ))}
                    </Carousel>
                </WingBlank>
                <WhiteSpace size="lg" />
                <NoticeBar mode="closable" action={<span style={{ color: '#a1a1a1' }}>不再提示</span>}>
                    天气转凉,试试新推出的xxx热饮吧
    </NoticeBar>
                <div style={{ padding: '15px 0' }}>
                    <WingBlank><PlaceHolder style={{ color: "black" }} /></WingBlank>
                </div>
                <div className="row" style={{ padding: "5px", marginTop: 5, height: 100 }}> <img style={{ height: 70, width: 70, borderRadius: 5 }} src={"../static/img/nai1.jpg"} /><p  style={{ flexGrow: "1",wordWrap:"break-word",width:"auto"}}>XXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX</p></div>
                <div className="row" style={{ padding: "5px", marginTop: 5, height: 100 }}> <img style={{ height: 70, width: 70, borderRadius: 5 }} src={"../static/img/nai2.jpg"} /> <p style={{ flexGrow: "1"}}>XXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX</p></div>

            </Fragment>
        );
    }

}

export default Home;
