import React, { Component, Fragment } from 'react';
import axios from 'axios';
import 'antd/dist/antd.css'
import { Avatar } from 'antd';


class Header extends Component {

    constructor(props) {
        super(props)
        this.state = {
            inputValue: '',
            list: ['asd', 'asdas', 'asdasdzwqed']
        }
    }

    render() {
        return (
            <Fragment>

                <div className="row">
                    <Avatar shape="square" size={70} src="../img/me.jpg" />
                    <div className="col" style={{ marginTop: '30px', marginLeft: '10px', background: '#03a9f4', borderRadius: '5px' }}>
                        <label className="header_item">博客數</label>
                        <label className="header_item "  >123456789</label>
                    </div>
                    <div className="col" style={{ marginTop: '30px', marginLeft: '10px', background: '#03a9f4', borderRadius: '5px' }}>
                        <label className="header_item">訪客數</label>
                        <label className="header_item"> 123456789</label>
                    </div>
                    <div className="row" >
                      <a>
                      <Avatar shape="square" size={50} src="../img/github.svg" />
                      </a>
                      <a>
                      <Avatar shape="square" size={20} src="../img/github.png" />
                      </a>
                    </div>

                    </div>
            </Fragment>
                );
            }
    handleInoutChange(e) {
                    this.setState({
                        inputValue: e.target.value
                    })
                }
                handleButtonClick(e) {
                    this.setState({
                        list: [...this.state.list, this.state.inputValue],
                        inputValue: ''
                    })
        axios.get('/miao/blog/list')
            .then(function (response) {
                    console.log(response);
                })
            .catch(function (error) {
                    console.log(error);
                });
        }
    handleLiClick(index) {
        const list = [...this.state.list]
                list.splice(index, 1)
        this.setState({
                    list: list
            })
    
        }
    }
    
    export default Header;
